      (result) => {
        this.setState({
          result: JSON.stringify(result),
        });
      },
      (error) => {
        this.setState({
          error
        });
      }
    )
}
render() {
  const { error, result } = this.state;
  const btn = (
    <div>
      <button onClick={() => this.getFetch()}>GET</button>
      <br />
      <button onClick={() => this.postFetch()}>POST</button>
    </div>);
  var content = (
    <div></div>
  );
  if (error) {
    content = <div>Error: {error.message}</div>;
  } else {
    content = <div>{result}</div>;
  }
  return (
    <div>{btn}{content}</div>
  );
}
...
```
